BeforeAll {
    . (Join-Path -Path $PSScriptRoot -ChildPath '_InitializeTests.ps1')
    . (Join-Path -Path $PrivateFolder -ChildPath 'Add-DiaHtmlSubGraph.ps1')
    . (Join-Path -Path $PrivateFolder -ChildPath 'Format-HtmlFontProperty.ps1')
    . (Join-Path -Path $PrivateFolder -ChildPath 'Format-HtmlTable.ps1')
    . (Join-Path -Path $PrivateFolder -ChildPath 'Format-NodeObject.ps1')

}

Describe Add-DiaHtmlSubGraph {
    BeforeAll {
        $Images = @{
            "Main_Logo" = "Diagrammer.png"
            "DomainController" = "AD_DC.png"
            "Subnets" = "AD_Subnets.png"
        }
        $DCsArray = @("Server-dc-01v", "Server-dc-02v", "Server-dc-03v", "Server-dc-04v", "Server-dc-05v", "Server-dc-06v")
        $SubnetsArray = @("192.168.5.0", "192.168.7.0", "192.168.9.0", "172.16.9.0", "172.16.18.0", "10.10.8.0")

        $DCNodes = Add-DiaHtmlNodeTable -Name "Subnet" -ImagesObj $Images -inputObject $DCsArray -ColumnSize 3 -Align 'Center' -iconType "DomainController" -MultiIcon
        $SubnetsNodes = Add-DiaHtmlNodeTable -Name "Subnet" -ImagesObj $Images -inputObject $SubnetsArray -ColumnSize 3 -Align 'Center' -iconType "Subnets" -MultiIcon

        $TableObjects = @($DCNodes, $SubnetsNodes)

        $HTMLSubgraphLogoDown = Add-DiaHtmlSubGraph -Name "Subnet" -ImagesObj $Images -TableArray $TableObjects -Align "Center"  -IconType 'Main_Logo' -ColumnSize 2 -Label 'SubGraph Test' -LabelPos 'down'
        $HTMLSubgraphLogoDownDebug = Add-DiaHtmlSubGraph -Name "Subnet" -ImagesObj $Images -TableArray $TableObjects -Align "Center"  -IconType 'Main_Logo' -ColumnSize 2 -Label 'SubGraph Test' -LabelPos 'down' -IconDebug $true

        $HTMLSubgraphLogoTop = Add-DiaHtmlSubGraph -Name "Subnet" -ImagesObj $Images -TableArray $TableObjects -Align "Center"  -IconType 'Main_Logo' -ColumnSize 2 -Label 'SubGraph Test' -LabelPos 'top'
        $HTMLSubgraphLogoTopDebug = Add-DiaHtmlSubGraph -Name "Subnet" -ImagesObj $Images -TableArray $TableObjects -Align "Center"  -IconType 'Main_Logo' -ColumnSize 2 -Label 'SubGraph Test' -LabelPos 'top' -IconDebug $true

        $HTMLSubgraphLogoTopTableBackgroundColor = Add-DiaHtmlSubGraph -Name "Subnet" -ImagesObj $Images -TableArray $TableObjects -Align "Center"  -IconType 'Main_Logo' -ColumnSize 2 -Label 'SubGraph Test' -LabelPos 'top' -TableBackgroundColor "#FFFFCC"

    }

    It "Should return a multiple column HTML table with Label and Logo at botton" {
        $HTMLSubgraphLogoDown | Should -BeExactly '<TABLE PORT="EdgeDot" STYLE="SOLID" BORDER="0" CELLBORDER="0" CELLSPACING="5" CELLPADDING="5" BGCOLOR="#ffffff" COLOR="#000000"><TR><TD align="Center" colspan="1"><FONT POINT-SIZE="14"><TABLE bgcolor="#FFFFFF" COLOR="#FFFFFF" STYLE="solid" PORT="EdgeDot" border="0" cellborder="0" cellpadding="5" cellspacing="5"><TR><TD ALIGN="Center" colspan="1"><img src="AD_DC.png"/></TD><TD ALIGN="Center" colspan="1"><img src="AD_DC.png"/></TD><TD ALIGN="Center" colspan="1"><img src="AD_DC.png"/></TD></TR><TR><TD BGCOLOR="#FFFFFF" PORT="Server-dc-01v" ALIGN="Center" colspan="1"><FONT FACE="Segoe Ui" POINT-SIZE="14" COLOR="#000000"><B>Server-dc-01v</B></FONT></TD><TD BGCOLOR="#FFFFFF" PORT="Server-dc-02v" ALIGN="Center" colspan="1"><FONT FACE="Segoe Ui" POINT-SIZE="14" COLOR="#000000"><B>Server-dc-02v</B></FONT></TD><TD BGCOLOR="#FFFFFF" PORT="Server-dc-03v" ALIGN="Center" colspan="1"><FONT FACE="Segoe Ui" POINT-SIZE="14" COLOR="#000000"><B>Server-dc-03v</B></FONT></TD></TR><TR><TD ALIGN="Center" colspan="1"><img src="AD_DC.png"/></TD><TD ALIGN="Center" colspan="1"><img src="AD_DC.png"/></TD><TD ALIGN="Center" colspan="1"><img src="AD_DC.png"/></TD></TR><TR><TD BGCOLOR="#FFFFFF" PORT="Server-dc-04v" ALIGN="Center" colspan="1"><FONT FACE="Segoe Ui" POINT-SIZE="14" COLOR="#000000"><B>Server-dc-04v</B></FONT></TD><TD BGCOLOR="#FFFFFF" PORT="Server-dc-05v" ALIGN="Center" colspan="1"><FONT FACE="Segoe Ui" POINT-SIZE="14" COLOR="#000000"><B>Server-dc-05v</B></FONT></TD><TD BGCOLOR="#FFFFFF" PORT="Server-dc-06v" ALIGN="Center" colspan="1"><FONT FACE="Segoe Ui" POINT-SIZE="14" COLOR="#000000"><B>Server-dc-06v</B></FONT></TD></TR></TABLE></FONT></TD><TD align="Center" colspan="1"><FONT POINT-SIZE="14"><TABLE bgcolor="#FFFFFF" COLOR="#FFFFFF" STYLE="solid" PORT="EdgeDot" border="0" cellborder="0" cellpadding="5" cellspacing="5"><TR><TD ALIGN="Center" colspan="1"><img src="AD_Subnets.png"/></TD><TD ALIGN="Center" colspan="1"><img src="AD_Subnets.png"/></TD><TD ALIGN="Center" colspan="1"><img src="AD_Subnets.png"/></TD></TR><TR><TD BGCOLOR="#FFFFFF" PORT="192.168.5.0" ALIGN="Center" colspan="1"><FONT FACE="Segoe Ui" POINT-SIZE="14" COLOR="#000000"><B>192.168.5.0</B></FONT></TD><TD BGCOLOR="#FFFFFF" PORT="192.168.7.0" ALIGN="Center" colspan="1"><FONT FACE="Segoe Ui" POINT-SIZE="14" COLOR="#000000"><B>192.168.7.0</B></FONT></TD><TD BGCOLOR="#FFFFFF" PORT="192.168.9.0" ALIGN="Center" colspan="1"><FONT FACE="Segoe Ui" POINT-SIZE="14" COLOR="#000000"><B>192.168.9.0</B></FONT></TD></TR><TR><TD ALIGN="Center" colspan="1"><img src="AD_Subnets.png"/></TD><TD ALIGN="Center" colspan="1"><img src="AD_Subnets.png"/></TD><TD ALIGN="Center" colspan="1"><img src="AD_Subnets.png"/></TD></TR><TR><TD BGCOLOR="#FFFFFF" PORT="172.16.9.0" ALIGN="Center" colspan="1"><FONT FACE="Segoe Ui" POINT-SIZE="14" COLOR="#000000"><B>172.16.9.0</B></FONT></TD><TD BGCOLOR="#FFFFFF" PORT="172.16.18.0" ALIGN="Center" colspan="1"><FONT FACE="Segoe Ui" POINT-SIZE="14" COLOR="#000000"><B>172.16.18.0</B></FONT></TD><TD BGCOLOR="#FFFFFF" PORT="10.10.8.0" ALIGN="Center" colspan="1"><FONT FACE="Segoe Ui" POINT-SIZE="14" COLOR="#000000"><B>10.10.8.0</B></FONT></TD></TR></TABLE></FONT></TD></TR><TR><TD valign="BOTTOM" ALIGN="Center" colspan="2" fixedsize="true" width="40" height="40"><IMG src="Diagrammer.png"></IMG></TD></TR><TR><TD valign="TOP" ALIGN="Center" colspan="2"><FONT FACE="Segoe Ui" POINT-SIZE="14" COLOR="#000000">SubGraph Test</FONT></TD></TR></TABLE>'
    }
