        $HTMLSubgraphLogoDownDebug | Should -BeExactly '<TABLE PORT="EdgeDot" STYLE="SOLID" BORDER="1" CELLBORDER="1" CELLSPACING="5" CELLPADDING="5" BGCOLOR="#ffffff" COLOR="red"><TR><TD align="Center" colspan="1"><FONT POINT-SIZE="14"><TABLE bgcolor="#FFFFFF" COLOR="#FFFFFF" STYLE="solid" PORT="EdgeDot" border="0" cellborder="0" cellpadding="5" cellspacing="5"><TR><TD ALIGN="Center" colspan="1"><img src="AD_DC.png"/></TD><TD ALIGN="Center" colspan="1"><img src="AD_DC.png"/></TD><TD ALIGN="Center" colspan="1"><img src="AD_DC.png"/></TD></TR><TR><TD BGCOLOR="#FFFFFF" PORT="Server-dc-01v" ALIGN="Center" colspan="1"><FONT FACE="Segoe Ui" POINT-SIZE="14" COLOR="#000000"><B>Server-dc-01v</B></FONT></TD><TD BGCOLOR="#FFFFFF" PORT="Server-dc-02v" ALIGN="Center" colspan="1"><FONT FACE="Segoe Ui" POINT-SIZE="14" COLOR="#000000"><B>Server-dc-02v</B></FONT></TD><TD BGCOLOR="#FFFFFF" PORT="Server-dc-03v" ALIGN="Center" colspan="1"><FONT FACE="Segoe Ui" POINT-SIZE="14" COLOR="#000000"><B>Server-dc-03v</B></FONT></TD></TR><TR><TD ALIGN="Center" colspan="1"><img src="AD_DC.png"/></TD><TD ALIGN="Center" colspan="1"><img src="AD_DC.png"/></TD><TD ALIGN="Center" colspan="1"><img src="AD_DC.png"/></TD></TR><TR><TD BGCOLOR="#FFFFFF" PORT="Server-dc-04v" ALIGN="Center" colspan="1"><FONT FACE="Segoe Ui" POINT-SIZE="14" COLOR="#000000"><B>Server-dc-04v</B></FONT></TD><TD BGCOLOR="#FFFFFF" PORT="Server-dc-05v" ALIGN="Center" colspan="1"><FONT FACE="Segoe Ui" POINT-SIZE="14" COLOR="#000000"><B>Server-dc-05v</B></FONT></TD><TD BGCOLOR="#FFFFFF" PORT="Server-dc-06v" ALIGN="Center" colspan="1"><FONT FACE="Segoe Ui" POINT-SIZE="14" COLOR="#000000"><B>Server-dc-06v</B></FONT></TD></TR></TABLE></FONT></TD><TD align="Center" colspan="1"><FONT POINT-SIZE="14"><TABLE bgcolor="#FFFFFF" COLOR="#FFFFFF" STYLE="solid" PORT="EdgeDot" border="0" cellborder="0" cellpadding="5" cellspacing="5"><TR><TD ALIGN="Center" colspan="1"><img src="AD_Subnets.png"/></TD><TD ALIGN="Center" colspan="1"><img src="AD_Sub